<?php

namespace AppBundle\Repository;

/**
 * SoftMainRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class SoftMainRepository extends \Doctrine\ORM\EntityRepository
{

    public function findAllActiveSoftwares(){
        $qb = $this->createQueryBuilder('s')
            ->where('isActive', true)
            ->getQuery();
        return $qb->getResult();
    }

    /**
     * @param $word
     * @return array
     */

    public function getSoftMainByName($word)
    {
        $softmain = strtolower("%" . $word . "%");

        $qb = $this->createQueryBuilder('s')
            ->select('s.name')
            ->where('lower(s.name) LIKE :name')
            ->setParameter('name', $softmain)
            ->getQuery();
        return $qb->getResult();
    }


    /**
     * @param $booleanKey
     * @param $entityName
     * @return array
     */
    public function getSoftByAnyBool($booleanKey, $entityName)
    {

        $qb = $this
            ->createQueryBuilder('s')
            ->join('s.' . $entityName , 't')
            ->where('s.isActive = true')
            ->andWhere('t.' . $booleanKey . ' = true')
            ->getQuery();
        return $qb->getResult();
    }

    /**
     * @param $softMainName
     * @param $booleanKey
     * @param $entityName
     * @return mixed
     */
    public function getBoolByAnySoft($softMainName, $booleanKey, $entityName)
    {

        $qb = $this
            ->createQueryBuilder('s')
            ->join('s.' . $entityName , 't')
            ->setParameter('name', $softMainName)
            ->where('s.name LIKE :name')
            ->andWhere('t.' . $booleanKey . ' = true')
            ->getQuery();
        return $qb->getResult();
    }

